import { useEffect, useState } from "react";
import { BiHeart, BiBookmark } from "react-icons/bi";
import { GoComment } from "react-icons/go";
import { format } from "timeago.js";

import { useDispatch, useSelector } from "react-redux";
import "./PostCard.styles.css";
import { Link } from "react-router-dom";
import { likePost, unLikePost } from "../../../../features/User/userLikesApi";
import { PostComments } from "./PostComments";
import {
  addBookmark,
  removeBookmark,
} from "../../../../features/User/userBookmarkApi";

export function PostCard({
  post,
  postCommentShow,
  isBookmarkIconShow,
  isLikeIconShow,
  children,
}) {

  

  const Dispatch = useDispatch();
  const { userID, token } = useSelector((state) => state.users);

  const [likeIconShow, userLikeIconShow] = useState(isLikeIconShow);
  const [bookmarkIconShow, userBookmarkIconShow] = useState(isBookmarkIconShow);


 

const checkIsPostSavedByUser = post?.bookmarkBy?.includes(userID);
const checkIsPostLikeByUser  = post?.likeBy?.includes(userID);
const { caption, imageUrl, createdAt } = post;
console.log(post)
  function likePostHandler() {
    Dispatch(likePost({ userID, token, postID: post._id }));
  }
  function unLikePostHander() {
    Dispatch(unLikePost({ userID, token, postID: post._id }));
  }
  function addBookmarkHandler() {
    Dispatch(addBookmark({ userID, token, postID: post._id }));
  }
  function removeBookmarkHandler() {
    Dispatch(removeBookmark({ userID, token, postID: post._id }));
  }

  return (
    <div className="post">
      <div className="post__header-user">
        <div className="post__avatar">
          <img
            className="create-post__avatar-image"
            alt=""
          x

          />
        </div>

        <div className="post__user-info">
          <span className="post__owner-name">
            {username}
          </span>
          <span className="post__created-at">{format(createdAt)}</span>
        </div>
      </div>

      <div className="post__header-caption">
        <span className="post-captions">{caption}</span>
      </div>

      <div className="post__image-info">
        {imageUrl !== null && (
          <Link to={`/post/${post._id}`} className="post__image-info">
            <img className="post__image" alt="" src={imageUrl} />
          </Link>
        )}
      </div>

      <div className="post__info">
        {/* <span className="post__comments-count">24k comments</span> */}

        <span className="post__likes-count">{post.likesCount} likes</span>
      </div>

      <div className="post__actions-info ">
        <div className="post__actions-comments pst-action">
          <Link
            to={`/post/${post._id}`}
            className="post__actions-comments-btn icon-bd-none"
          >
            <GoComment size="1.5rem" />
          </Link>
          <span className="post__actions-name pst-action-name">Comments</span>
        </div>

        {likeIconShow && (
          <>
            {" "}
            { checkIsPostLikeByUser ? (
              <div className="post__actions-likes pst-action">
                <button
                  onClick={unLikePostHander}
                  className="post__actions-like-btn icon-bd-none"
                >
                  <BiHeart size="1.5rem" color="red" />
                </button>
                <span className="post__actions-name pst-action-name">
                  Liked
                </span>
              </div>
            ) : (
              <div className="post__actions-likes pst-action">
                <button
                  onClick={likePostHandler}
                  className="post__actions-like-btn icon-bd-none"
                >
                  <BiHeart size="1.5rem" />
                </button>
                <span className="post__actions-name pst-action-name">Like</span>
              </div>
            )}
          </>
        )}

        {bookmarkIconShow && (
          <>
            {checkIsPostSavedByUser ? (
              <div className="post__actions-saved pst-action">
                <button
                  onClick={removeBookmarkHandler}
                  className="post__actions-save-btn icon-bd-none"
                >
                  <BiBookmark size="1.5rem" color="red" />
                </button>
                <span className="post__actions-name pst-action-name">
                  Saved{" "}
                </span>
              </div>
            ) : (
              <div className="post__actions-saved pst-action">
                <button
                  onClick={addBookmarkHandler}
                  className="post__actions-save-btn icon-bd-none"
                >
                  <BiBookmark size="1.5rem" />
                </button>
                <span className="post__actions-name pst-action-name">
                  Save{" "}
                </span>
              </div>
            )}
          </>
        )}
      </div>
      {children}
    </div>
  );
}
