import { createAsyncThunk } from "@reduxjs/toolkit";
import axios from 'axios';


export const getUserLikes = createAsyncThunk("userLikes/getUserLikes" , async(apiPermission)=>{
const { token , userID } = apiPermission;
const url = `https://media.imukulsaini.repl.co/v1/${userID}/likes`;
const headers= {
    authorization:token
}
console.log('user like me haIA')
const userResponse = await axios.get(url,{headers});
console.log(userResponse.data)
return userResponse.data;

})

export const likePost = createAsyncThunk("userLikes/likePost" , async (postPermission) => {

    const { token , userID,postID } = postPermission;
    const url = `https://media.imukulsaini.repl.co/v1/${userID}/likes`;
    const headers= {
        authorization:token
    }
    const userResponse = await axios.post(url,{postID},{headers});
    return userResponse.data;
    
    })

    export const unLikePost = createAsyncThunk("userLikes/unLikePost" , async (postPermission) => {

        const { token , userID, postID } = postPermission ;

        const url = `https://media.imukulsaini.repl.co/v1/${userID}/likes`;
        const headers= {
            authorization:token
        }
        const userResponse = await axios.delete(url, { 
            headers,
            data: {
              postID
            }
          });
        return userResponse.data ;
        
        })
