import { useParams } from "react-router";
import { newCommentIsAdded } from "../../../../features/post/PostCommentsSlice";
import { createNewComment } from "../../../../features/post/PostCommentsApi";




export function PostCreateComment({ postCommentRoom }) {
  const params = useParams();

  const { userID, token, userData } = useSelector((state) => state.users);

  const socket = useRef();
  const Dispatch = useDispatch();

  useEffect(() => {
    
      socket.current = io("https://media.imukulsaini.repl.co");
      console.log("new connection for posts");
    
