import "./Messages.css";
import { NavBar } from "../navbar/nav";
import { RiSendPlaneFill } from "react-icons/ri";
import { useEffect, useState, useRef } from "react";
import {
  getUserMessage,
  sendNewMessage,
} from "../../../../features/Message/MessageApi";
import { getConversation } from "../../../../features/Message/ConversationApi";
import { format } from "timeago.js";
import { useDispatch, useSelector } from "react-redux";
import { newMessageReceived } from "../../../../features/Message/MessageSlice";

import io from "socket.io-client";

function UserConversations({ user, messages }) {
  const { message, createdAt, firstName } = messages;

  return (
    <>
      <div
        className={
          user
            ? "message__conversation-info user"
            : "message__conversation-info "
        }
      >
        <div className="message__user-avatar-info">
          <img
            alt=""
            className="message__user-image"
            src="data:image/png;base64,"
          />
        </div>
        <span className="message__user-conversation">{message}</span>
      </div>
      <div
        className={
          user
            ? "message__conversation-time user"
            : "message__conversation-time"
        }
      >
        {format(createdAt)}
      </div>
    </>
  );
}

function FriendsInfo({ friend }) {
  return (
    <div className="message__friends-info">
      <div className="message__friends-avatar">
        <img
          className="friend-info__friends-avatar"
          alt=""
          src="data:image/png;base64,"
        />
      </div>
      <div className="message__friends-name">
        <span className="friends-name">{friend?.firstName}</span>
      </div>
    </div>
  );
}

function ConversationRoom({ conversationRoom, userID }) {
  const filterFriend = conversationRoom.filter((fri) => fri._id !== userID);

  return filterFriend?.map((friend) => {
    return (
      <div key={friend._id} className="conversation__friends-info">
        <FriendsInfo friend={friend} />
      </div>
    );
  });
}

export function Messages() {
  const [text, setText] = useState("");
  const [conversationID, setConversationID] = useState("");

  // const [socket , setSocket ] = useState('');

  // const socket = io("https://media.imukulsaini.repl.co");

  const { conversationRoom, status } = useSelector(
    (state) => state.conversation
  );
  const { messages } = useSelector((state) => state.messages);
  const { userID, token } = useSelector((state) => state.users);
  const dispatch = useDispatch();

  const scrollLastMessage = useRef();
  const socket = useRef();

  function getUserMessages(roomID) {
    dispatch(getUserMessage({ conversationID: roomID, userID, token }));
    setConversationID(roomID);
  }
  function sendMessage() {
    const a = conversationRoom.filter((con) => con._id == conversationID);
    const { members } = a[0];
    const getReceiverID = members.filter((user) => user._id !== userID);

    socket.current.emit("sendMessage", {
      senderID: userID,
      message: text,
      createdAt: Date.now(),
      receiverID: getReceiverID[0]._id,
    });

    dispatch(sendNewMessage({ conversationID, userID, token, text }));

    setText("");
  }

  useEffect(() => {
    socket.current = io('https://media.imukulsaini.repl.co', { transports : ['websocket'] });
    
  }, []);

    useEffect(() => {
    console.log("use Effect from new Message");
    socket.current.on("getNewMessage", (newMessage) => {
      console.log("new message from this ==== ", newMessage);

      dispatch(newMessageReceived(newMessage));
    });
  }, []);

  useEffect(() => {
    socket.current.emit("addUsers", userID);
    socket.current.on("getOnlineUsers", (users) => {
      console.log(users);
    });
  }, [userID]);

  useEffect(() => {
    dispatch(getConversation({ userID, token }));
  }, [dispatch, token, userID]);

  useEffect(() => {
    scrollLastMessage?.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <div className="message">
      <NavBar />

      <section className="message__friends">
        <div className="message__friends-heading">conversation</div>

        {conversationRoom?.map((room) => {
          return (
            <div
              key={room._id}
              onClick={(e) => getUserMessages(room._id)}
              className="message__con-room"
            >
              <ConversationRoom
                conversationRoom={room.members}
                userID={userID}
              />
            </div>
          );
        })}

        <button className="load-friends__btn">Load More</button>
      </section>

      <section className="message__conversation">
        <div className="message__wrapper">
          {messages?.map((message) => {
            return message.senderID === userID ? (
              <div key={message.id} ref={scrollLastMessage}>
                <UserConversations messages={message} user={true} />
              </div>
            ) : (
              <div key={message._id} ref={scrollLastMessage}>
                <UserConversations messages={message} />
              </div>
            );
          })}

          {/* { messages?.map(message=>{
          return message.senderID === userID ? 
          
          <UserConversations 
          key={message._id}
          messages={message}
          user={true}
          /> : <UserConversations 
            messages={message}
            key={message._id}
          />
         })} */}
        </div>

        <div className="message__conversation-input">
          <input
            value={text}
            onChange={(event) => setText(event.target.value)}
            className="conversation-input"
            placeholder="message to "
          />
          <button
            onClick={() => sendMessage()}
            className="conversation-send__btn"
          >
            <RiSendPlaneFill color="blue" size="1.5rem" />
          </button>
        </div>
      </section>

      <section className="message__friend-profile">
        <div className="message__friend-avatar">
          <img
            className="friend-avatar"
            alt=""
            src="data:image/png;base64,"
          />
        </div>
        <div className="message__friend-name">
          <span className="friend-name">Natalie Anderson</span>
        </div>
        <div className="message__friend-status">Active</div>
        <div className="message__friend-actions">
          <button className="show-profile-btn">View full profile</button>
        </div>
      </section>
    </div>
  );
}
